mapper.select:
SELECT count(1) FROM ( SELECT t.TENDER_TYPE, b.NAME AS borrowName, b.ID AS borrowId, b.USER_ID AS borrowUserid, m.USERNAME AS borrowUsername, t.ACCOUNT AS tenderAccount, FROM_UNIXTIME(t.ADDTIME) AS succcessTime FROM rocky_borrow AS b, rocky_member AS m, rocky_b_tenderrecord AS t WHERE b.USER_ID = m.ID AND t.BORROW_ID = b.ID AND (b.STATUS = 4 OR b.STATUS = 5 OR b.STATUS = 42) AND (b.APPRSTATUS IN (3, 4) OR (b.APPRSTATUS = 2 AND b.TIMING_BORROW_TIME IS NOT NULL)) AND t.PARENT_ID IS NULL
AND t.USER_ID = #{userId}
UNION ALL SELECT t.TENDER_TYPE, b.NAME AS borrowName, b.ID AS borrowId, b.USER_ID AS borrowUserid, m.USERNAME AS borrowUsername, rbs.ACCOUNT AS tenderAccount, FROM_UNIXTIME(t.ADDTIME) AS succcessTime FROM rocky_borrow AS b, rocky_member AS m, rocky_b_tenderrecord AS t, rocky_b_subscribe rbs WHERE b.USER_ID = m.ID AND rbs.TENDER_ID = t.ID AND rbs.STATUS = 3 AND t.BORROW_ID = b.ID AND (b.STATUS = 4 OR b.STATUS = 5 OR b.STATUS = 42) AND (b.APPRSTATUS IN (3, 4) OR (b.APPRSTATUS = 2 AND b.TIMING_BORROW_TIME IS NOT NULL)) AND t.PARENT_ID IS NOT NULL
AND t.USER_ID = #{userId}
) AS temp order by succcessTime desc

SELECT * FROM ( SELECT t.TENDER_TYPE, b.NAME AS borrowName, b.ID AS borrowId, b.USER_ID AS borrowUserid, m.USERNAME AS borrowUsername, m.USERIDMD5 AS userIdMD5, t.ACCOUNT AS tenderAccount, FROM_UNIXTIME(t.ADDTIME) AS succcessTime FROM rocky_borrow AS b, rocky_member AS m, rocky_b_tenderrecord AS t WHERE b.USER_ID = m.ID AND t.BORROW_ID = b.ID AND (b.STATUS = 4 OR b.STATUS = 5 OR b.STATUS = 42) AND (b.APPRSTATUS IN (3, 4) OR (b.APPRSTATUS = 2 AND b.TIMING_BORROW_TIME IS NOT NULL)) AND t.PARENT_ID IS NULL
AND t.USER_ID = #{userId}
UNION ALL SELECT t.TENDER_TYPE, b.NAME AS borrowName, b.ID AS borrowId, b.USER_ID AS borrowUserid, m.USERNAME AS borrowUsername, m.USERIDMD5 AS userIdMD5, rbs.ACCOUNT AS tenderAccount, FROM_UNIXTIME(t.ADDTIME) AS succcessTime FROM rocky_borrow AS b, rocky_member AS m, rocky_b_tenderrecord AS t, rocky_b_subscribe rbs WHERE b.USER_ID = m.ID AND rbs.TENDER_ID = t.ID AND rbs.STATUS = 3 AND t.BORROW_ID = b.ID AND (b.STATUS = 4 OR b.STATUS = 5 OR b.STATUS = 42) AND (b.APPRSTATUS IN (3, 4) OR (b.APPRSTATUS = 2 AND b.TIMING_BORROW_TIME IS NOT NULL)) AND t.PARENT_ID IS NOT NULL
AND t.USER_ID = #{userId}
) AS temp order by succcessTime desc

SELECT fixBorrow.ID as fixBorrowId,fixBorrow.CONTRACT_NO,fixBorrow.LOCK_LIMIT, detail.TENDER_TYPE,detail.ADDTIME,fixBorrow.PUBLISH_TIME,detail.ACCOUNT as tenderAccount from t_fix_borrow fixBorrow LEFT JOIN (SELECT TENDER_TYPE,ACCOUNT,ADDTIME,USER_ID,fix_borrow_id,STATUS from t_fix_tender_detail b
where user_id=#{userId}
) detail on (fixBorrow.id = detail.fix_borrow_id) where detail.STATUS in(0,1,3)
and detail.USER_ID=#{userId}
order by detail.addtime DESC

SELECT count(1)
from t_fix_borrow fixBorrow LEFT JOIN
(SELECT TENDER_TYPE,ADDTIME,USER_ID,fix_borrow_id,STATUS from
t_fix_tender_detail b 

  where user_id=#{userId}

  )
detail on (fixBorrow.id = detail.fix_borrow_id)
where  detail.STATUS in(0,1,3) 

   and detail.USER_ID=#{userId}
