select b.`NAME` borrow_name, b.`STATUS` borrow_status, b.style style, b.TIME_LIMIT timeLimit,b.BORROWTYPE borrowType, m.username, , (SELECT SUM(c.CAPITAL) FROM rocky_b_collectionrecord c WHERE c.IS_FIRST_BORROW = 1 AND c.BORROW_ID = b.id AND c.`ORDER` = r.`ORDER`) first_account from rocky_b_repaymentrecord r,rocky_borrow b ,rocky_member m where r.BORROW_ID = b.id and b.BORROWTYPE in(1,2,5) and b.`STATUS` in(4,42,5) and b.USER_ID = m.id
and r.REPAYMENT_TIME >= #{beignTime}
and r.REPAYMENT_TIME <= #{endTime}
ORDER BY r.status asc, r.REPAYMENT_TIME asc

  		SELECT count(r.id)
		FROM rocky_b_repaymentrecord r,rocky_borrow b
		WHERE r.BORROW_ID = b.id and b.BORROWTYPE in(1,2,5) and b.`STATUS` in(4,42,5) 
  		
			and r.REPAYMENT_TIME >= #{beignTime}
		
		
			and r.REPAYMENT_TIME <= #{endTime}

SELECT SUM(CASE WHEN `STATUS` = 0 THEN REPAYMENT_ACCOUNT END) AS noRepaymentAccount, SUM(CASE WHEN `STATUS` = 1 THEN REPAYMENT_ACCOUNT END) AS yesRepaymentAccount, SUM(REPAYMENT_ACCOUNT) AS repaymentAccount FROM ( SELECT br.`STATUS`, br.REPAYMENT_ACCOUNT FROM rocky_b_repaymentrecord br, rocky_borrow b WHERE br.BORROW_ID = b.id AND b.BORROWTYPE IN (1, 2, 5)
AND br.REPAYMENT_TIME >= #{beignTime}
AND br.REPAYMENT_TIME <= #{endTime}
) t
