mapper.update:
  		UPDATE t_first_borrow
		SET REAL_ACCOUNT  = REAL_ACCOUNT - #{unlockaccount}
		WHERE ID = #{id}

  		UPDATE t_first_borrow
		SET STATUS  = IF(REAL_ACCOUNT > 0,STATUS,-5)
		WHERE ID = #{id}

mapper.select:
SELECT  FROM (
		SELECT * FROM (
			SELECT * FROM t_first_borrow WHERE `STATUS` = 3 AND PUBLISH_TIME   <=  #{now} AND END_TIME   >=  NOW() ORDER BY ID DESC LIMIT 1
		) t1
		UNION
		SELECT * FROM (
			SELECT * FROM t_first_borrow WHERE `STATUS` = 3 AND PUBLISH_TIME   >  #{now} ORDER BY ID ASC LIMIT 1
		) t2
		UNION
		SELECT * FROM (
			SELECT * FROM t_first_borrow WHERE `STATUS` in(3,5) ORDER BY ID DESC LIMIT 1
		) t3 LIMIT 1
) f
