mapper.select:
SELECT count(tr.id) FROM t_first_tender_real tr LEFT JOIN ( /** 直通车投标冻结 */ SELECT t.FIRST_TENDER_REAL_ID,IFNULL(SUM(t.ACCOUNT),0) AS tenderMoney FROM rocky_b_tenderrecord t,rocky_borrow b WHERE 1=1 AND t.STATUS = 0 AND t.USER_ID = #{userId} AND b.ID = t.BORROW_ID AND b.STATUS IN(2,3) AND t.TENDER_TYPE=2 GROUP BY t.FIRST_TENDER_REAL_ID ) t ON tr.id=t.FIRST_TENDER_REAL_ID LEFT JOIN ( /** 直通车待收最小还款日*/ SELECT c.FIRST_TENDER_REAL_ID,MIN(REPAY_TIME) AS minRepayTime FROM rocky_b_collectionrecord c WHERE c.status=0 AND c.USER_ID=#{userId} AND c.FIRST_TENDER_REAL_ID IS NOT NULL AND c.IS_FIRST_BORROW =1 GROUP BY c.FIRST_TENDER_REAL_ID ) bc ON tr.id=bc.FIRST_TENDER_REAL_ID WHERE 1=1 AND tr.STATUS =0 AND tr.USER_ID =#{userId} AND t.tenderMoney IS NULL AND tr.PARENT_ID IS NULL AND (bc.minRepayTime is null or CONCAT(DATE_ADD(FROM_UNIXTIME(bc.minRepayTime,'%Y-%m-%d'),INTERVAL -1 DAY),' 22:30:00') > STR_TO_DATE(NOW(),'%Y-%m-%d %H:%i:%S'))
and tr.ADDTIME >= #{beginTime}
and tr.ADDTIME <= concat(#{endTime},' 23:59:59')
and tr.id = #{firstTenderRealId}
FOR UPDATE

SELECT temp.occupyInterest as interest, temp.interestDiff, tr.ACCOUNT+IFNULL(temp.occupyInterest,0)-IFNULL(temp.interestDiff,0) AS bondsAccount,
FROM ( SELECT FROM t_first_tender_real tr LEFT JOIN ( /** 直通车投标冻结 */ SELECT t.FIRST_TENDER_REAL_ID,IFNULL(SUM(t.ACCOUNT),0) AS tenderMoney FROM rocky_b_tenderrecord t,rocky_borrow b WHERE 1=1 AND t.STATUS = 0 AND t.USER_ID = #{userId} AND b.ID = t.BORROW_ID AND b.STATUS IN(2,3) AND t.TENDER_TYPE=2 GROUP BY t.FIRST_TENDER_REAL_ID ) t ON tr.id=t.FIRST_TENDER_REAL_ID LEFT JOIN ( /** 直通车待收最小还款日*/ SELECT c.FIRST_TENDER_REAL_ID,MIN(REPAY_TIME) AS minRepayTime FROM rocky_b_collectionrecord c WHERE c.status=0 AND c.USER_ID=#{userId} AND c.FIRST_TENDER_REAL_ID IS NOT NULL AND c.IS_FIRST_BORROW =1 GROUP BY c.FIRST_TENDER_REAL_ID ) bc ON tr.id=bc.FIRST_TENDER_REAL_ID WHERE 1=1 AND tr.STATUS =0 AND tr.USER_ID =#{userId} AND t.tenderMoney IS NULL AND (bc.minRepayTime is null or CONCAT(DATE_ADD(FROM_UNIXTIME(bc.minRepayTime,'%Y-%m-%d'),INTERVAL -1 DAY),' 22:30:00') > STR_TO_DATE(NOW(),'%Y-%m-%d %H:%i:%S'))
and tr.ADDTIME >= #{beginTime}
and tr.ADDTIME <= concat(#{endTime},' 23:59:59')
) tr LEFT JOIN ( SELECT SUM(ROUND(ROUND(re.orderInterest/orderDays,8)*re.occupyDays,2)) AS occupyInterest, SUM(ROUND(ROUND(ROUND(re.orderInterest/orderDays,8)*re.occupyDays,8)*b.APR/100/${yearDays}*re.unOccupyDays,2)) AS interestDiff, re.FIRST_TENDER_REAL_ID FROM( SELECT t.id, FROM_UNIXTIME(IFNULL(bc.REPAY_TIME,t.ADDTIME),'%Y-%m-%d %H:%i:%s') AS collectionBeginTime, FROM_UNIXTIME(t.REPAY_TIME,'%Y-%m-%d %H:%i:%s') AS collectionEndtime, NOW() AS firstTransferAddTime, CASE WHEN DATEDIFF(DATE_FORMAT(NOW(),'%Y-%m-%d'),FROM_UNIXTIME(IFNULL(bc.REPAY_TIME,t.ADDTIME),'%Y-%m-%d')) < 0 THEN 0 ELSE DATEDIFF(DATE_FORMAT(NOW(),'%Y-%m-%d'),FROM_UNIXTIME(IFNULL(bc.REPAY_TIME,t.ADDTIME),'%Y-%m-%d')) END AS occupyDays, DATEDIFF(FROM_UNIXTIME(t.REPAY_TIME,'%Y-%m-%d'),DATE_FORMAT(NOW(),'%Y-%m-%d')) AS unOccupyDays, DATEDIFF(FROM_UNIXTIME(t.REPAY_TIME,'%Y-%m-%d'),FROM_UNIXTIME(IFNULL(bc.REPAY_TIME,t.ADDTIME),'%Y-%m-%d')) AS orderDays, t.INTEREST AS orderInterest, t.borrow_id, t.FIRST_TENDER_REAL_ID FROM ( SELECT c.* FROM rocky_b_collectionrecord c , ( SELECT @rn := IF(@borrowId = c.BORROW_ID and @tenderId = c.TENDER_ID and @firstTenderRealId = c.FIRST_TENDER_REAL_ID, @rn+1, 1) AS rownum, @borrowId := c.BORROW_ID,@tenderId := c.TENDER_ID,@firstTenderRealId := c.FIRST_TENDER_REAL_ID,c.id FROM rocky_b_collectionrecord c LEFT JOIN ( /** 直通车待收最小还款日*/ SELECT c.FIRST_TENDER_REAL_ID,MIN(REPAY_TIME) AS minRepayTime FROM rocky_b_collectionrecord c WHERE c.status=0 AND c.USER_ID=#{userId} AND c.FIRST_TENDER_REAL_ID IS NOT NULL AND c.IS_FIRST_BORROW =1
and c.FIRST_TENDER_REAL_ID = #{firstTenderRealId}
GROUP BY c.FIRST_TENDER_REAL_ID ) bc ON (c.FIRST_TENDER_REAL_ID = bc.FIRST_TENDER_REAL_ID), (SELECT @rn := 1, @borrowId := 0,@tenderId := 0,@firstTenderRealId := 0) X WHERE c.status=0 AND c.USER_ID=#{userId} AND c.FIRST_TENDER_REAL_ID IS NOT NULL AND c.IS_FIRST_BORROW =1 AND (bc.minRepayTime is null or CONCAT(DATE_ADD(FROM_UNIXTIME(bc.minRepayTime,'%Y-%m-%d'),INTERVAL -1 DAY),' 22:30:00') > STR_TO_DATE(NOW(),'%Y-%m-%d %H:%i:%S'))
and c.FIRST_TENDER_REAL_ID = #{firstTenderRealId}
ORDER BY c.FIRST_TENDER_REAL_ID,c.BORROW_ID,c.TENDER_ID,c.order ) tempc WHERE 1=1 AND c.id=tempc.id AND tempc.rownum=1 ) t LEFT JOIN rocky_b_collectionrecord bc ON (bc.order=t.order-1 AND bc.BORROW_ID = t.BORROW_ID AND bc.TENDER_ID = t.TENDER_ID AND bc.user_id=t.user_id AND bc.FIRST_TENDER_REAL_ID = t.FIRST_TENDER_REAL_ID) ) re,rocky_borrow b WHERE re.borrow_id=b.id GROUP BY re.FIRST_TENDER_REAL_ID ) temp ON tr.id=temp.FIRST_TENDER_REAL_ID where 1=1 AND tr.PARENT_ID IS NULL
and tr.id = #{firstTenderRealId}
ORDER BY tr.ADDTIME DESC

SELECT 	
	  TR.ORDERNUM,RM.USERNAME,
	 ,
	 ROUND(FT.AWARDS*100/FT.ACCOUNT, 2) as awardApr
FROM   T_FIRST_TRANSFER FT
INNER JOIN t_first_tender_real TR ON TR.ID = FT.FIRST_TENDER_REAL_ID
LEFT JOIN ROCKY_MEMBER RM ON RM.ID = FT.USER_ID


	AND FT.ID = #{id}



	ORDER BY FT.ADD_TIME DESC


	ORDER BY FT.ACCOUNT_REAL ${orderType}


	ORDER BY FT.ADD_TIME ${orderType}


	ORDER BY FT.AWARDS ${orderType}


	ORDER BY ROUND(FT.AWARDS*100/FT.ACCOUNT, 2) ${orderType}


	FOR UPDATE

SELECT FT.ID, FT.ACCOUNT_REAL, FT.AWARDS, FT.MANAGE_FEE, FT.SUCCESS_TIME, R.ADDTIME AS firstOpenTime, R.ORDERNUM FROM T_FIRST_TRANSFER FT INNER JOIN T_FIRST_SUBSCRIBE FS ON FS.FIRST_TRANSFER_ID = FT.ID INNER JOIN T_FIRST_TENDER_REAL R ON FS.NEW_FIRST_TENDER_REAL_ID = R.ID WHERE FS.STATUS = 3
AND FT.STATUS = #{transferStatus}
AND FS.USER_ID = #{userId}
AND R.ADDTIME >= #{beginTime}
AND R.ADDTIME <= concat(#{endTime},' 23:59:59')
ORDER BY FT.SUCCESS_TIME DESC

SELECT COUNT(FT.ID) FROM T_FIRST_TRANSFER FT INNER JOIN T_FIRST_SUBSCRIBE FS ON FS.FIRST_TRANSFER_ID = FT.ID INNER JOIN T_FIRST_TENDER_REAL R ON FS.NEW_FIRST_TENDER_REAL_ID = R.ID WHERE FS.STATUS = 3
AND FT.STATUS = #{transferStatus}
AND FS.USER_ID = #{userId}
AND R.ADDTIME >= #{beginTime}
AND R.ADDTIME <= concat(#{endTime},' 23:59:59')

SELECT R.ADDTIME AS firstOpenTime, R.ORDERNUM, M.USERNAME,MS.USERNAME AS SUBSCRIBE_USERNAME,
FROM T_FIRST_TRANSFER FT INNER JOIN T_FIRST_SUBSCRIBE FS ON FS.FIRST_TRANSFER_ID = FT.ID INNER JOIN T_FIRST_TENDER_REAL R ON FS.NEW_FIRST_TENDER_REAL_ID = R.ID INNER JOIN ROCKY_MEMBER M ON M.ID = FT.USER_ID INNER JOIN ROCKY_MEMBER MS ON MS.ID = FS.USER_ID WHERE FS.STATUS = 3
AND FT.STATUS = #{transferStatus}
AND FS.USER_ID = #{userId}
AND R.ADDTIME >= #{beginTime}
AND R.ADDTIME <= concat(#{endTime},' 23:59:59')
ORDER BY FT.SUCCESS_TIME DESC

SELECT COUNT(FT.ID)
FROM   T_FIRST_TRANSFER FT
       INNER JOIN T_FIRST_SUBSCRIBE FS
         ON FS.FIRST_TRANSFER_ID = FT.ID
WHERE FS.STATUS = 3

	AND FT.STATUS = #{transferStatus}


	AND FS.USER_ID = #{userId}


 	AND R.ADDTIME >= #{beginTime} 


 	AND R.ADDTIME <= concat(#{endTime},' 23:59:59')
