mapper.select:
select SUM(tempstatic.REPAY_YESACCOUNTYES) repayYesAccountYes ,SUM(tempstatic.CAPITAL) capital,SUM(tempstatic.REPAY_YESACCOUNTNO) repayYesAccountNo from (
select IFNULL(SUM(c.REPAY_YESACCOUNT-c.CAPITAL),0) + IFNULL(fc.INTEREST,0) AS REPAY_YESACCOUNTYES , '' CAPITAL,'' REPAY_YESACCOUNTNO from t_fix_collectionrecord c LEFT JOIN t_float_coupon fc ON c.FIX_TENDER_REAL_ID = fc.TARGET_ID AND c.FIX_BORROW_ID = fc.BIZ_ID AND fc.`STATUS`=2 and fc.TARGET_TYPE = 2 where c.status ='1' and c.USER_ID=#{userId}
union all select '',IFNULL(SUM(account),0),'' from t_fix_tender_detail where status in(0,1) and USER_ID=#{userId}
union all select '','',IFNULL(SUM(c.INTEREST),0) + IFNULL(ROUND(fc.CAPITAL*fc.APR_YEAR*b.LOCK_LIMIT/100/12,2),0) from t_fix_collectionrecord c LEFT JOIN t_float_coupon fc ON c.FIX_TENDER_REAL_ID = fc.TARGET_ID AND c.FIX_BORROW_ID = fc.BIZ_ID AND fc.`STATUS`=1 and fc.TARGET_TYPE = 2 , t_fix_borrow b where c.status ='0' and c.USER_ID=#{userId} AND c.FIX_BORROW_ID = b.ID ) tempstatic

mapper.update:
update rocky_account ra
set ra.total = ra.total - #{account},
ra.use_money = ra.use_money - #{account},
ra.DRAW_MONEY=ra.DRAW_MONEY - #{drawMoney},
ra.NO_DRAW_MONEY=ra.NO_DRAW_MONEY - #{noDrawMoney}
where user_id = #{userId}

mapper.select:
 SELECT tfb.MOST_ACCOUNT- IFNULL(tft.hasJoinMoney,0) from t_fix_borrow tfb left join 
( SELECT FIX_BORROW_ID,user_Id,sum(account) hasJoinMoney from t_fix_tender_detail where user_id = #{userId} GROUP BY FIX_BORROW_ID,USER_ID ) tft  on (tfb.id =tft.fix_borrow_id  )

where  tfb.id  =#{fixBorrowId}
