sql.if:
			AND (
			(c.STATUS in(1,3) 
			
				and c.REPAY_YESTIME >= #{beginTime}
				and c.REPAY_YESTIME < #{endTime}
			 
            )
            OR 
            (c.STATUS =2 and r.IS_REPAIR_INTEREST = 0 
             
				and  UNIX_TIMESTAMP(c.ADVANCETIME) >= #{beginTime}
				and  UNIX_TIMESTAMP(c.ADVANCETIME) < #{endTime}
			 
            ) 
            )

mapper.select:
SELECT SUM(c.REPAY_ACCOUNT) AS SUM_REPAY_ACCOUNT, SUM(c.REPAY_YESACCOUNT) AS SUM_REPAY_YESACCOUNT, SUM(c.CAPITAL) AS SUM_CAPITAL, SUM(c.INTEREST) AS SUM_INTEREST
FROM rocky_b_collectionrecord c LEFT JOIN rocky_b_tenderrecord t ON t.ID = c.TENDER_ID LEFT JOIN rocky_borrow b ON b.ID = t.BORROW_ID LEFT JOIN rocky_member rm ON b.USER_ID = rm.ID LEFT JOIN t_first_borrow f ON f.ID = c.FIRST_BORROW_ID LEFT JOIN rocky_b_repaymentrecord r ON r.BORROW_ID = b.ID WHERE 1 = 1
AND c.ORDER = r.ORDER ;

SELECT count(1)
FROM rocky_b_collectionrecord c 
WHERE  user_id =#{userId} and borrow_id =#{borrowId}
