mapper.update:
		update t_lottery_use_record set status = 2
		where 1=1
		and user_id=${userId}
		and AWARD_TYPE=2
		and id =${id}

update t_lottery_use_record set status = 1,remark=#{remark}
where 1=1
and user_id=${userId}
and AWARD_TYPE=2
and id =${lott_id}

mapper.select:
select t1.ADD_TIME as rewardTime,IFNULL(t1.AWARD_MONEY,0) as rewardMoney,t1.ID as id, t1.LOTTERY_GOODS_NAME as rewardType, case when t1.status=0 then '未发放' when t1.status=1 then '已领取' when t1.status=2 then '受理中' when t1.status=3 then '派发中' else '已过期' end as rewardStatus, case when t1.AWARD_TYPE=1 then IF(t1.status=0,'使用','不可操作') when t1.AWARD_TYPE=2 then IF(t1.status=0,'领取',IF(t1.status=3,'确认收货','不可操作')) when t1.AWARD_TYPE=4 then '不可操作' when t1.AWARD_TYPE=5 then '不可操作' when t1.AWARD_TYPE=6 then '不可操作' end as rewardopert, t3.NAME as rewardSource from t_lottery_use_record t1 INNER JOIN t_lottery_chance_info t2 on t1.LOTTERY_ID = t2.id INNER JOIN t_lottery_chance_rule_info t3 on t2.LOTTERY_CHANCE_RULE_INFO_ID = t3.id where 1=1 and (t1.AWARD_TYPE=1 or t1.AWARD_TYPE=2 or t1.AWARD_TYPE=4 or t1.AWARD_TYPE=5 or t1.AWARD_TYPE=6) and t1.user_id = ${userId}
and t1.AWARD_TYPE=#{awardType}
order by t1.ADD_TIME desc

select count(1) from (select t1.ADD_TIME as rewardTime, t1.LOTTERY_GOODS_NAME as rewardType, case when t1.status=0 then '未发放' when t1.status=1 then '已领取' when t1.status=2 then '受理中' when t1.status=3 then '派发中' else '已过期' end as rewardStatus, case when t1.AWARD_TYPE=1 then IF(t1.status=0,'使用','不可操作') when t1.AWARD_TYPE=2 then IF(t1.status=0,'领取',IF(t1.status=3,'确认收货','不可操作')) when t1.AWARD_TYPE=4 then '不可操作' when t1.AWARD_TYPE=5 then '不可操作' when t1.AWARD_TYPE=6 then '不可操作' end as rewardopert, t3.NAME as rewardSource from t_lottery_use_record t1 INNER JOIN t_lottery_chance_info t2 on t1.LOTTERY_ID = t2.id INNER JOIN t_lottery_chance_rule_info t3 on t2.LOTTERY_CHANCE_RULE_INFO_ID = t3.id where 1=1 and (t1.AWARD_TYPE=1 or t1.AWARD_TYPE=2 or t1.AWARD_TYPE=4 or t1.AWARD_TYPE=5 or t1.AWARD_TYPE=6) and t1.user_id = ${userId}
and t1.AWARD_TYPE=#{awardType}
order by t1.ADD_TIME desc) t2
