mapper.insert:
update rocky_member
set LOGINTIMES = LOGINTIMES + 1,
    LASTLOGINTIME = UNIX_TIMESTAMP(),
    LASTLOGINIP = #{ip}
where ID = #{id}

mapper.update:
UPDATE rocky_member
	SET IS_CUSTODY = #{isCustody},
		E_TYPE = #{eType}
	WHERE ID = #{id}
		AND STATUS = #{status}

update rocky_member set GAINACCUMULATEPOINTS = GAINACCUMULATEPOINTS + 3, ACCUMULATEPOINTS = ACCUMULATEPOINTS + 3
where ID = #{id}
and exists (select 1 from rocky_member_accumulate_points where TYPE = 0 and MEMBERID = #{id} and GAINDATE >= curdate() and GAINDATE < (curdate() + interval 1 day) limit 1)
