mapper.select:
SELECT  
	 
FROM rocky_b_repaymentrecord r
WHERE r.id = #{id}

SELECT SUM(r.REPAYMENT_ACCOUNT) repaymentAccountTotal,SUM(r.CAPITAL) capitalTotal,SUM(r.INTEREST) interestTotal, SUM(r.LATE_INTEREST) lateInterestTotal FROM ( SELECT r.REPAYMENT_ACCOUNT,r.CAPITAL,r.INTEREST, CASE WHEN r.status = 0 AND TIMESTAMPDIFF(DAY,DATE_FORMAT(FROM_UNIXTIME(r.REPAYMENT_TIME),'%Y-%m-%d'),CURDATE()) > 0 THEN round(r.REPAYMENT_ACCOUNT*0.002*TIMESTAMPDIFF(DAY,DATE_FORMAT(FROM_UNIXTIME(r.REPAYMENT_TIME),'%Y-%m-%d'),CURDATE()),2) ELSE r.LATE_INTEREST END LATE_INTEREST FROM ROCKY_B_REPAYMENTRECORD r,ROCKY_BORROW RB WHERE r.BORROW_ID = RB.ID
AND RB.NAME LIKE CONCAT(#{borrowName}, '%' )
) r

  
	SELECT COUNT(r.ID) 
	FROM rocky_b_repaymentrecord r
	WHERE r.STATUS = 0
	AND r.ORDER < #{order}
	AND r.BORROW_ID = #{borrowId}

SELECT 
	ifnull(SUM(r.REPAYMENT_ACCOUNT),0) sumRepaymentAccount,	
	ifnull(SUM(r.CAPITAL),0) sumCapital,
	ifnull(SUM(r.interest),0) sumInterest,
	ifnull(SUM(r.late_interest),0) sumLateInterest
FROM 
	ROCKY_B_REPAYMENTRECORD r 
LEFT JOIN 
	ROCKY_BORROW RB ON r.BORROW_ID = RB.ID
WHERE 1=1 


	 AND rb.status = #{borrowStatus}



	 AND r.webStatus = #{webStatus}



	 AND r.borrow_Id = #{borrowId}



	 AND r.status = #{status}



	 AND RB.NAME LIKE  CONCAT('%', #{keyword}, '%' )



	 AND r.USER_ID = #{userID}



	AND cast(r.REPAYMENT_TIME as signed) > #{beginTime}



	AND cast(r.REPAYMENT_TIME as signed)*1000 < #{endTime}


ORDER BY 

	r.order desc,

r.status asc, r.REPAYMENT_TIME asc
