mapper.select:
	select count(1) from  gq_apply_info t where 1=1 
	
		AND t.USER_ID = #{userId}
	
	
		AND t.`status` = #{status}
	
	
		AND t.type = #{type}
	
	
      and DATE_FORMAT(t.addtime, '%Y-%m-%d')>=  DATE_FORMAT(#{beginTime}, '%Y-%m-%d')   
    
    
      and DATE_FORMAT(t.addtime, '%Y-%m-%d')<=  DATE_FORMAT(#{endTime}, '%Y-%m-%d')   
    
	  ORDER BY t.addtime DESC

	select * from  gq_apply_info t where 1=1 
	
		AND t.USER_ID = #{userId}
	
	
		AND t.`status` = #{status}
	
	
		AND t.type = #{type}
	
	
      and DATE_FORMAT(t.addtime, '%Y-%m-%d')>=  DATE_FORMAT(#{beginTime}, '%Y-%m-%d')   
    
    
      and DATE_FORMAT(t.addtime, '%Y-%m-%d')<=  DATE_FORMAT(#{endTime}, '%Y-%m-%d')   
    
	  ORDER BY t.addtime DESC

  	select count(1)
	from rocky_black_list b
	where b.status = 1 
	
		and b.user_id = #{userId,jdbcType=INTEGER} 
	
	
		and b.type = #{type,jdbcType=INTEGER}

SELECT * FROM gq_apply_info 
where user_id = #{userId,jdbcType=INTEGER} 
and type = 2 order by  addtime DESC limit 1;

  	select *
	from gq_apply_info b
	where 1=1
	
		and b.user_id = #{userId,jdbcType=INTEGER} 
	
	
		and b.type = #{type,jdbcType=INTEGER} 
	
  
         AND b.STATUS in
         #{sta}
   
   ORDER BY b.addtime desc LIMIT 1
