mapper.select:
select id, user_id, stock_code, stock_name, IFNULL(total,0) as total, IFNULL(use_stock,0) as useStock, IFNULL(no_use_stock,0) as noUseStock, remark, status, addtime, updatetime from gq_stock_account where status = 1
AND id = #{id,jdbcType=INTEGER}
AND user_id = #{userId,jdbcType=INTEGER}
for update

  	select IFNULL(sum(REPAY_ACCOUNT),0) as sanBiao from rocky_b_collectionrecord 
	where USER_ID = #{userId ,jdbcType=INTEGER}
	 and `STATUS` = 0 ;

select IFNULL(sum(REPAY_ACCOUNT),0) dingQiBao from t_fix_collectionrecord 
where USER_ID = #{userId ,jdbcType=INTEGER}
and `STATUS` = 0;
