	for update

select 

from gq_stock_entrust
where 1=1

    and user_id = #{userId}

 
      AND status  in
      #{sta}

select *
from gq_stock_entrust
where 1=1

    and user_id = #{userId ,jdbcType=INTEGER}


    and entrust_type = #{entrustType ,jdbcType=INTEGER}

 
    and status = #{status ,jdbcType=INTEGER}

 
    and date_format(addtime,'%Y-%m-%d')  >= #{startDate ,jdbcType=VARCHAR}
 
  
    and date_format(addtime,'%Y-%m-%d')  <= #{endDate ,jdbcType=VARCHAR}
 

   and residue_amount > 0 and (status =1 or status=2)

 
   order by price DESC

 
   order by price ASC

 
   order by addtime DESC


   order by addtime ASC

select count(1)
from gq_stock_entrust
where 1=1

    and user_id = #{userId}


    and entrust_type = #{entrustType ,jdbcType=INTEGER}

 
    and status = #{status ,jdbcType=INTEGER}

  
    and date_format(addtime,'%Y-%m-%d')  >= #{startDate ,jdbcType=VARCHAR}
 
  
    and date_format(addtime,'%Y-%m-%d')  <= #{endDate ,jdbcType=VARCHAR}

select *
from gq_stock_entrust
where (status = 1 or status = 2)

	and price >=  #{price ,jdbcType=DECIMAL}
    and entrust_type = #{entrustType ,jdbcType=INTEGER}
    and user_id  != #{userId ,jdbcType=INTEGER}
    and residue_amount > 0
    order by price DESC , addtime ASC 

 
    and price <=  #{price ,jdbcType=DECIMAL}
    and entrust_type = #{entrustType ,jdbcType=INTEGER}
    and user_id  != #{userId ,jdbcType=INTEGER}
    and residue_amount > 0
     order by price ASC , addtime ASC 

 for update

