mapper.select:
select user_name as username,user_namep as usernamep,reg_time as regTime,logging_type as type from tzj_member_binding where 1=1
and add_time >= #{startTime} and add_time <= #{endTime}
and user_name in (${usernameStr})

select t.*,(t.totalAmount-t.balanceAmount) as awaitAmount from ( select user_name as username,user_namep as usernamep,FROM_UNIXTIME(reg_time,'%Y-%m-%d %H:%i:%S') as registerAt, FROM_UNIXTIME(add_time,'%Y-%m-%d %H:%i:%S') as bindAt,logging_type as bindType,a.use_money as balanceAmount, (a.DRAW_MONEY+a.NO_DRAW_MONEY+a.NO_USE_MONEY+a.COLLECTION+a.FIRST_BORROW_USE_MONEY+IFNULL(cur.TOTAL,0)+IFNULL(fix.fixCapitalTotal,0)+IFNULL(coll.fixInvestNoTotal,0)) as totalAmount from tzj_member_binding tmb LEFT JOIN rocky_account a on tmb.user_id=a.USER_ID LEFT JOIN t_cur_account cur on tmb.user_id = cur.USER_ID LEFT JOIN (SELECT USER_ID,SUM(c.ACCOUNT) as fixCapitalTotal FROM t_fix_tender_detail c where c.status in(0,1) GROUP by USER_ID) fix on tmb.user_id = fix.USER_ID LEFT JOIN (SELECT USER_ID,SUM(co.INTEREST) as fixInvestNoTotal FROM t_fix_collectionrecord co where co.status=0 GROUP by USER_ID) coll on tmb.user_id = coll.USER_ID where dr=1 and IS_SUCCESS=1
and user_name in(${arrayValStr})
and FROM_UNIXTIME(add_time)>=#{startTime}
and FROM_UNIXTIME(add_time)<=#{endTime}
) t order by t.bindAt ASC
